package com.hokanosekai.engine.gfx;public class ImageTile extends Image{    private int tileW, tileH;    public ImageTile(String path, int tileX, int tileH){        super(path);        this.tileW = tileX;        this.tileH = tileH;    }    public int getTileW() {        return tileW;    }    public void setTileW(int tileW) {        this.tileW = tileW;    }    public int getTileH() {        return tileH;    }    public void setTileH(int tileH) {        this.tileH = tileH;    }}